// Helper para armar URLs públicas (funciona en dev y en GitHub Pages)
export const BASE = process.env.PUBLIC_URL || '';

export const assetUrl = (p) => {
  const base = BASE.replace(/\/$/, '');
  const path = String(p || '').replace(/^\//, '');
  return base ? `${base}/${path}` : `/${path}`;
};

export const FALLBACK = assetUrl('assets/logo.png');

export const handleImgError = (e, fallbackSrc = FALLBACK) => {
  const img = e.currentTarget;
  img.onerror = null;  // evita loop si fallara el fallback
  img.src = fallbackSrc;
};
